  var recipesArray = [];
  await Firebase.firestore()
    .collection("recipes")
    .get()
    .then((snapshot) => {
       recipesArray = snapshot.docs.map(doc => {
        return doc.data();
      })})
  console.log(recipesArray)
  return recipesArray;
}

export function addRecipe(recipe){
  Firebase.firestore()
  .collection("recipes").add({
    recipeId:recipe.recipeId,
    categoryId: recipe.categoryId,
    title: recipe.title,
    photo_url: recipe.photo_url,
    photosArray: recipe.photosArray,
    time: recipe.time,
    ingredients: recipe.ingredients.map(ingredient=>{
      return {
        id: ingredient[0],
        amount: ingredient[1]
      }
    }),
    description: recipe.description
  })
}

export function addAllRecipes(recipes){
  recipes.map(recipe=>{
    addRecipe(recipe)
  })
}

export function addCategory(category){
  Firebase.firestore()
  .collection("categories").add({
    id: category.id,
    name: category.name,
    photo_url:category.photo_url
  })
}

export function addCategories(categories){
  categories.map(categories=>{
    addCategory(categories)
  })
}

export function addIngredient(ingredient){
  Firebase.firestore()
  .collection("ingredients").add({
    ingredientId: ingredient.ingredientId,
    name: ingredient.name,
    photo_url:ingredient.photo_url
  })
}

export function addIngredients(ingredients){
  ingredients.map(ingredient=>{
    addIngredient(ingredient)
  })
}


